wfrp4e.script:
if (args.type == "skill" && args.item.characteristic.key == "ag")
   args.prefillModifiers.modifier -= 10

if (args.type == "characteristic" && args.item == "ag")
   args.prefillModifiers.modifier -= 10

if(args.opposedTest.result.hitloc.value == "head" && (args.opposedTest.attackerTest.result.critical || args.actor.status.wounds.value - args.totalWoundLoss < 0))
{

  if (this.actor.isOwner) {
    args.actor.setupSkill("Tempra", { absolute: { difficulty: "average" } }).then(setupData => {
      args.actor.basicTest(setupData).then(test => {
        if (test.result.outcome == "failure")
          args.actor.addCondition("unconscious")
      })
    })
  }
}

if(this.actor.isOwner) {
    args.actor.setupSkill("Tempra", { absolute: { difficulty: "average" } }).then(setupData => {
      args.actor.basicTest(setupData).then(test => {
        if (test.result.outcome == "failure") {
          args.actor.addCondition("prone")
          fromUuid("Compendium.wfrp4e-core.injuries.ZhMADOqoo0y8Q9bx").then(injury => {
               injury = injury.toObject()
               injury.system.location.value = `${Math.clamped(Math.abs(test.result.SL) + 1, 1, 5)} ${injury.system.location.value}`
              args.actor.createEmbeddedDocuments("Item", [injury])
          })
        }
      })
    })
  }

fromUuid("Compendium.wfrp4e-core.injuries.zyocWSzEZEC826NS").then(injury => {
 args.actor.createEmbeddedDocuments("Item", [injury.toObject()])
})


if (this.actor.isOwner)
 args.actor.setupSkill("Tempra", {absolute: {difficulty: "hard"}}).then(setupData => {
       args.actor.basicTest(setupData).then(test => 
        {
            if (test.result.outcome == "failure")
            { 
                   args.actor.addCondition("prone")
                  args.actor.addCondition("stunned")
             }
        })
     })

fromUuid("Compendium.wfrp4e-core.injuries.fBcZhOBn8IpoVqQ1").then(async injury => {
injury = injury.toObject()
 let teeth = (await new Roll("1d10").roll()).total
 ChatMessage.create({content : `<b>${this.actor.prototypeToken.name}</b> perdi ${teeth} denti`, speaker : {alias: this.effect.label}})
injury.system.location.value = `${teeth} ${injury.system.location.value}`
 args.actor.createEmbeddedDocuments("Item", [injury])
})

fromUuid("Compendium.wfrp4e-core.injuries.rkJA1DlK51QuRlJy").then(injury => {
 args.actor.createEmbeddedDocuments("Item", [injury.toObject()])
})

