    if (caster&& (args.actor.has(game.i18n.localize("NAME.Undead")) || args.actor.has(game.i18n.localize("NAME.Daemonic"))))
    {
         let wp = caster.characteristics.wp.value
         if (wp > args.actor.characteristics.t.value)
         {
             if (args.actor.has(game.i18n.localize("NAME.Unstable")))
             {
                args.actor.update({"system.status.wounds.value" : 0})
                args.actor.addCondition("dead")
             }
             else
             {
                fromUuid("Compendium.wfrp4e-core.traits.D0ImWEIMSDgElsnl").then(item => {
                    args.actor.createEmbeddedDocuments("Item", [item.toObject()])
                    ChatMessage.create({content : `Added Unstable to ${args.actor.prototypeToken.name}`, speaker : {alias: caster.name}})
                })
             }
         }
    }
 })

