wfrp4e.script:
// Remove the miscast if doubles rolled and succeeded
if(args.test.result.outcome == "success" && args.test.result.minormis && args.test.result.roll % 11 == 0)
{
    delete args.test.result.minormis
}
// Decrement the major miscast to minor miscast
else if(args.test.result.outcome == "success" && args.test.result.majormis && args.test.result.roll % 11 == 0)
{
    delete args.test.result.majormis
    args.test.result.minormis = game.i18n.localize("ROLL.MinorMis")
}

if (args.test.item && args.test.item.name == "Charm" &&  (args.test.result.roll <= 5 || args.test.result.roll <= args.test.target))
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
   args.test.result.other.push(`<b>${this.effect.label}</b>: ${ones + args.test.successBonus + args.test.slBonus} SL if debating.`)
}

if (args.test.item && args.test.item.name == "Charm" &&  (args.test.result.roll <= 5 || args.test.result.roll <= args.test.target))
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
   args.test.result.other.push(`<b>${this.effect.label}</b>: ${ones + args.test.successBonus + args.test.slBonus} SL if target is attracted to you.`)
}

let initiativeSetting = game.settings.get("wfrp4e", "initiativeRule")

switch (initiativeSetting) {
  case "default":
    args.initiative += "+10"
    break;

  case "sl":
    args.initiative += "+1"
    break;

  case "d10Init":
    args.initiative += "+10"
    break;

  case "d10InitAgi":
    args.initiative += "+1"
    break;
}

// Remove the miscast if doubles rolled and succeeded
if(args.test.result.outcome == "success" && args.test.result.minormis && args.test.result.roll % 11 == 0)
{
    delete args.test.result.minormis
}
// Decrement the major miscast to minor miscast
else if(args.test.result.outcome == "success" && args.test.result.majormis && args.test.result.roll % 11 == 0)
{
    delete args.test.result.majormis
    args.test.result.minormis = game.i18n.localize("ROLL.MinorMis")
}

if (args.sizeDiff <= -2 && args.opposedTest.attackerTest.result.critical)
        args.damageMultiplier = Math.abs(args.sizeDiff)

      let sBonusDiff = args.opposedTest.defenderTest.actor.characteristics.t.bonus - args.opposedTest.attackerTest.actor.characteristics.s.bonus
     let weapon = args.opposedTest.attackerTest.item
      if (sBonusDiff > 0 && weapon && weapon.damage.value.includes("SB"))
        args.damage += sBonusDiff

if (args.test.item && args.test.characteristicKey== "fel" &&  (args.test.result.roll <= 5 || args.test.result.roll <= args.test.target))
{

let SL = Math.floor(args.test.target / 10) - Math.floor(args.test.result.roll / 10)
let ones = Number(args.test.result.roll.toString().split("").pop())

if (ones > SL)
   args.test.result.other.push(`<b>${this.effect.label}</b>: ${ones + args.test.successBonus + args.test.slBonus} SL if influencing those with a higher status.`)
}

if(args.opposedTest.attackerTest.weapon.attackType == "ranged")
{
    let applyAP = (args.damageType == game.wfrp4e.config.DAMAGE_TYPE.IGNORE_TB || args.damageType == game.wfrp4e.config.DAMAGE_TYPE.NORMAL)


    let AP = args.AP

    if (applyAP) {
        newUsed = Math.max(AP.used - this.item.Advances, 0)
        args.totalWoundLoss += (AP.used - newUsed)

    let apIndex = args.messageElements.findIndex(i => i.includes(game.i18n.localize("AP")))
    args.messageElements[apIndex] = newUsed + "/" + AP.value + " " + game.i18n.localize("AP")
    }
}

if (args.type == "characteristic") {
      if (args.options.dodge && this.actor.isMounted)
          args.prefillModifiers.modifier += 20
    }

    if (args.type == "skill") {
      if (args.item.name == game.i18n.localize("NAME.Dodge") && this.actor.isMounted)
        args.prefillModifiers.modifier += 20

    }
